mapper.update:
update comment
set aid = #{aid,jdbcType=INTEGER},
  `uid` = #{uid,jdbcType=INTEGER},
  `date` = #{date,jdbcType=TIMESTAMP},
  content = #{content,jdbcType=LONGVARCHAR}
where cid = #{cid,jdbcType=INTEGER}

mapper.select:
select cid, aid, `uid`, `date`, content
from comment
where cid = #{cid,jdbcType=INTEGER}
